    ListaFrutas f = new ListaFrutas();
    private void btnFrutaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFrutaActionPerformed
        
        String frutaDigitada = txtFruta.getText();
        f.verificarFruta(frutaDigitada);
        
        if (f.getListaFrutas().contains(frutaDigitada)) {
            
            lblResultado.setText(
            String.format("A fruta %s existe", frutaDigitada)
            );
            lblResultado.setForeground(Color.BLUE);
        } else {
            lblResultado.setText(
            String.format("A fruta %s n√£o existe", frutaDigitada)
            );
            lblResultado.setForeground(Color.RED);
        }
    }//GEN-LAST:event_btnFrutaActionPerformed

